var HUMANAPI_APP_ID     =  '3e098d19e42de61584e1bac7ff321a7385edd094';
var HUMANAPI_APP_SECRET =  'd5f5725383e96b2d6fd8db5897b8535b2ac69ca6' ;

var moment = require('moment');

//moment.duration.subtract(5, 'm' );
//moment.subtract(5, 'm' );
moment.duration().subtract(5, 'm');

